javaposse.jobdsl.plugin.ExecuteDslScript.scriptText:
job('orcas_integration_test') {   
    description 'orcas core tests'
    steps {
      gradle {
            tasks('test')
            useWrapper = true
            configure { node ->
              (node / 'rootBuildScriptDir').setValue('/opt/orcas/orcas-master/orcas_integrationstest')
              (node / 'buildFile').setValue('/opt/orcas/orcas-master/orcas_integrationstest/build.gradle')
              (node / 'makeExecutable').setValue('true')
             }
        }
    }
    publishers {
        archiveJunit('**/testresult.xml')
    }  
}
job('orcas_spool_test') {     
    description 'orcas test tests (tests the capabiltiy of the test framework to detect relevant schema difeereneces)'
    steps {
         ant {
            prop('ladespooltest','*')
            prop('bindir','/tmp/workspace/orcas_spool_test/bin')
            buildFile('/opt/orcas/orcas-master/orcas_integrationstest/testspool/build.xml')
        }
    }    
    publishers {
        archiveJunit('**/*testresult.xml')
    }      
}
job('orcas_examples') {   
    description 'orcas example tests, simply executes all examples testing for no errors'
    steps {
         ant {
            buildFile('/opt/orcas/orcas-master/examples/build.xml')
        }
    }    
}
